const { getIp, getPort } = require('../index')

test('getIp Return string and is iP address', () => {
  expect(
    !!(getIp().split && getIp().split('.').length === 4)
  ).toBe(true)
})

test('getPort number', async () => {
  const p = await getPort(3000)
  expect(
    typeof p
  ).toBe("number")
})